mapper.update:
update tb_dict
set dict = #{dict,jdbcType=VARCHAR},
  type = #{type,jdbcType=INTEGER},
  created = #{created,jdbcType=TIMESTAMP},
  updated = #{updated,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, dict, type, created, updated
from tb_dict
where id = #{id,jdbcType=INTEGER}
