mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tb_item_desc (created, updated, item_desc
  )
values (#{created,jdbcType=TIMESTAMP}, #{updated,jdbcType=TIMESTAMP}, #{itemDesc,jdbcType=LONGVARCHAR}
  )

mapper.update:
update tb_item_desc
set created = #{created,jdbcType=TIMESTAMP},
  updated = #{updated,jdbcType=TIMESTAMP},
  item_desc = #{itemDesc,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, created, updated, item_desc
from tb_item_desc
where id = #{id,jdbcType=BIGINT}
